<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    $eventTitle = $_POST['Eventtitle'];\n    $description = $_POST['Description'];\n    $days = isset($_POST['day']) ? $_POST['day'] : [];\n\n    echo 'Event Title: ' . htmlspecialchars($eventTitle) . '<br>';\n    echo 'Description: ' . htmlspecialchars($description) . '<br>';\n    echo 'Days: <br>';\n    \n    foreach ($days as $day) {\n        echo htmlspecialchars($day) . '<br>';\n    }\n}\n?>