require 'vendor/autoload.php';\n\nuse Neos\MathEval\MathEvaluator;\n\nfunction safeCalculator($str) {\n    $evaluator = new MathEvaluator();\n    try {\n        $result = $evaluator->evaluate($str);\n        return $result;\n    } catch (Exception $e) {\n        return "Error in expression: " . $e->getMessage();\n    }\n}\n\n// Example usage\necho safeCalculator('3 + 5 * (2 - 8)'); // Output: -13