<?php\n// Assuming $db_con is your MySQLi connection\nif (!$db_con) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n// Perform the initial query with SQL_CALC_FOUND_ROWS\n$result = $db_con->query("SELECT SQL_CALC_FOUND_ROWS * FROM users LIMIT 0, 10");\n\nif (!$result) {\n    die("Query failed: " . $db_con->error);\n}\n\n// Perform the query to get the total count of rows\n$count_result = $db_con->query("SELECT FOUND_ROWS() as totalcount");\n\nif (!$count_result) {\n    die("Count query failed: " . $db_con->error);\n}\n\n$row = $count_result->fetch_assoc();\n$total = $row['totalcount'];\n\n// Output the total count\necho "Total rows available: " . $total;\n\n// Close the result sets\n$result->free();\n$count_result->free();\n\n// Close the database connection\n$db_con->close();\n?>