<?php\n// Load your XML string\n$xmlString = <<<XML\n<car>\n    <tire>Michelin</tire>\n    <seats>Leather</seats>\n    <type>\n        <color>red</color>\n        <make>Audi</make>\n    </type>\n</car>\nXML;\n\n// Create a new DOMDocument and load the XML\n$doc = new DOMDocument();\n$doc->loadXML($xmlString);\n\n// Get the <type> element\n$typeNode = $doc->getElementsByTagName('type')->item(0);\n\n// Save the outer XML of the <type> node\n$outerXML = $doc->saveXML($typeNode);\n\necho $outerXML;\n?>