// Define the categories\n$categories = ['fake', 'ok', 'sold', 'other'];\n\n// Initialize an array to hold the counts\n$categoryCounts = [];\n\n// Prepare the query\n$countAuctionsQuery = "SELECT COUNT(*) FROM AUCTIONS WHERE username = ? AND SUBCAT = ?";\n\nif ($getRecords = $con->prepare($countAuctionsQuery)) {\n    foreach ($categories as $subcat) {\n        // Bind parameters\n        $getRecords->bind_param("ss", $username, $subcat);\n        $getRecords->execute();\n        // Bind result\n        $getRecords->bind_result($numRecords);\n        $getRecords->fetch();\n        // Store the result in the associative array\n        $categoryCounts[$subcat] = $numRecords;\n    }\n    $getRecords->close();\n}\n\n// Print out the results\nforeach ($categories as $subcat) {\n    echo ucfirst($subcat) . " items: " . $categoryCounts[$subcat] . " items<br>";\n}