<?php\n// Detect the browser language\n$lang = substr($_SERVER['HTTP_ACCEPT_LANGUAGE'], 0, 2);\n\n// Determine the redirect URL based on the detected language\nswitch ($lang){\n    case "fr":\n        header("Location: /fr/index.htm", true, 302);\n        exit();\n    case "es":\n        header("Location: /es/index.htm", true, 302);\n        exit();\n    // Add more cases for other languages as needed\n    default:\n        header("Location: /en/index.htm", true, 302);\n        exit();\n}\n?>