<?php\n// Database connection details\n$host = 'localhost';\n$dbname = 'm118';\n$username = 'your_username';\n$password = 'your_password';\n\n// Create a new PDO instance\ntry {\n    $pdo = new PDO("mysql:host=$host;dbname=$dbname;charset=utf8", $username, $password);\n    // Set the PDO error mode to exception\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    die("Connection failed: " . $e->getMessage());\n}\n\n// Path to the CSV file\n$csvFile = 'path_to_your_csv_file.csv';\n\n// Open the CSV file in read mode\nif (($handle = fopen($csvFile, 'r')) !== FALSE) {\n    // Skip the first row (header)\n    fgetcsv($handle);\n\n    // Prepare the SQL statement (using placeholders for security)\n    $stmt = $pdo->prepare("UPDATE catalog_product_entity SET sku = :sku WHERE id = :id");\n\n    // Loop through the CSV file rows\n    while (($data = fgetcsv($handle, 1000, ',')) !== FALSE) {\n        // Bind the CSV data to the SQL statement\n        $stmt->bindParam(':id', $data[0], PDO::PARAM_INT);\n        $stmt->bindParam(':sku', $data[1], PDO::PARAM_STR);\n\n        // Execute the SQL statement\n        $stmt->execute();\n    }\n\n    // Close the file handle\n    fclose($handle);\n} else {\n    die("Could not open the CSV file.");\n}\n\n// Close the database connection\n$pdo = null;\n\necho "CSV data imported successfully.";\n?>