session_start();\n\n// Get existing session data\n$existing_items = isset($_SESSION['items']) ? $_SESSION['items'] : array();\n\n// Get new data from the form\n$new_items = isset($_POST['items']) ? $_POST['items'] : array();\n\n// Merge the new data with the existing session data\nforeach ($new_items as $key => $value) {\n    if (!empty($value)) {\n        $existing_items[$key] = $value;\n    }\n}\n\n// Update the session data\n$_SESSION['items'] = $existing_items;