class URISplit {\n    var $REQ_URI;\n\n    //some more variables\n\n    function __construct($uri) {\n        //some code\n        $this->REQ_URI = $uri;\n        //some code yet again\n    }\n}\n\nclass URIResolve extends URISplit {\n    //some variables\n\n    function __construct($uri) {\n        parent::__construct($uri);\n        //some code\n    }\n}\n\nclass PageControl extends URIResolve {\n    //some variables\n\n    function __construct($uri) {\n        parent::__construct($uri);\n        //some more code\n    }\n}\n\n// Creating an object of PageControl\n$page = new PageControl($_SERVER['REQUEST_URI']);