<?php\n$array1 = array(false, 200);\n$array2 = array(true, 80, 60, 75, 50, 0, 0, 30, 40);\n\n$array3 = array();\n$index1 = 0;\n$index2 = 0;\n\nwhile ($index1 < count($array1) || $index2 < count($array2)) {\n    if (isset($array1[$index1]) && $array1[$index1] !== false && $array1[$index1] !== null) {\n        $array3[] = $array1[$index1];\n        $index1++;\n    } else {\n        if (isset($array2[$index2])) {\n            $array3[] = $array2[$index2];\n            $index2++;\n        }\n        $index1++;\n    }\n}\n\nprint_r($array3);\n?>