<?php\n/*\nPlugin Name: Custom Student Community Plugin\nDescription: Adds custom functionalities to the student community site.\nVersion: 1.0\nAuthor: Your Name\n*/\n\n// Add a custom shortcode\nfunction custom_welcome_message() {\n    return "<h2>Welcome to the Student Community!</h2>";\n}\nadd_shortcode('welcome_message', 'custom_welcome_message');\n\n// Add custom post type for universities\nfunction create_university_post_type() {\n    register_post_type('university',\n        array(\n            'labels'      => array(\n                'name'          => __('Universities'),\n                'singular_name' => __('University'),\n            ),\n            'public'      => true,\n            'has_archive' => true,\n        )\n    );\n}\nadd_action('init', 'create_university_post_type');\n?>