function process_page_content($page_content) {\n    // Define a regex to recognize the placeholders for including PHP scripts\n    $pattern = '/{{include:(.*?)}}/';\n\n    // Use a callback function for regex replacement\n    $page_content = preg_replace_callback($pattern, function($matches) {\n        // Sanitize the filename to avoid directory traversal attacks\n        $filename = basename($matches[1]);\n\n        // Define the path to the script\n        $filepath = __DIR__ . '/includes/' . $filename;\n\n        // Check if the file exists\n        if (file_exists($filepath)) {\n            // Capture the output of the included file\n            ob_start();\n            include($filepath);\n            $output = ob_get_clean();\n\n            // Return the captured output\n            return $output;\n        } else {\n            // Return an error message if the file does not exist\n            return "<!-- Error: file $filename not found -->";\n        }\n    }, $page_content);\n\n    return $page_content;\n}\n\n// Example usage\n$page_content = '<html>{{include:header.php}}</html>';\necho process_page_content($page_content);