// Set the default timezone to the server's default timezone\ndate_default_timezone_set('America/Los_Angeles'); // Example timezone\n\n// Convert UTC time to the default timezone\n$utcTime = '2023-10-13 12:00:00';\n$date = new DateTime($utcTime, new DateTimeZone('UTC'));\n$date->setTimezone(new DateTimeZone(date_default_timezone_get()));\n\n$localTime = $date->format('Y-m-d H:i:s');\n\necho "Local time: " . $localTime;