<?php\n// Ensure the Intl extension is available\nif (!extension_loaded('intl')) {\n    die('The Intl extension is not loaded.');\n}\n\n// Function to check if a string contains only alphabetic characters\nfunction isAlpha($string) {\n    // Convert the string to an array of Unicode code points\n    $codepoints = mb_convert_encoding($string, 'UTF-32BE', 'UTF-8');\n    $length = mb_strlen($codepoints, 'UTF-32BE');\n\n    for ($i = 0; $i < $length; $i++) {\n        $char = mb_substr($codepoints, $i, 1, 'UTF-32BE');\n        $ord = unpack('N', $char)[1];\n        \n        if (!IntlChar::isalpha($ord)) {\n            return false; // Non-alphabetic character found\n        }\n    }\n\n    return true; // All characters are alphabetic\n}\n\n// Example usage\n$testString = "你好"; // Chinese text\nif (isAlpha($testString)) {\n    echo "The string contains only alphabetic characters.";\n} else {\n    echo "The string contains non-alphabetic characters.";\n}\n?>