require_once "Auth/OpenID/Consumer.php";\nrequire_once "Auth/OpenID/FileStore.php";\n\n// Set up the OpenID consumer\n$store = new Auth_OpenID_FileStore('/tmp/_php_consumer_test');\n$consumer = new Auth_OpenID_Consumer($store);\n\n// The OpenID URL provided by the user\n$openid_url = 'http://flim.blogspot.com';\n\n// Begin the OpenID authentication process\n$auth_request = $consumer->begin($openid_url);\n\nif (!$auth_request) {\n    echo "Authentication error; not a valid OpenID.";\n} else {\n    // Redirect the user to the OpenID provider for authentication\n    $redirect_url = $auth_request->redirectURL('http://example.com', 'http://example.com/return_to');\n    header("Location: " . $redirect_url);\n}