<?php\n// Define the directory containing the JavaScript files\n$jsDir = 'includes/js/';\n\n// Get all JavaScript files from the directory\n$jsFiles = glob($jsDir . '*.js');\n\n// Initialize a variable to hold the combined contents\n$combinedJs = '';\n\n// Loop through each JavaScript file and append its contents to the combined variable\nforeach ($jsFiles as $jsFile) {\n    $combinedJs .= file_get_contents($jsFile) . "\n";\n}\n\n// Minify the combined JavaScript content\n$minifiedJs = minifyJs($combinedJs);\n\n// Save the minified JavaScript to a single file\nfile_put_contents('combined.min.js', $minifiedJs);\n\n// Function to minify JavaScript content\nfunction minifyJs($js) {\n    // Remove comments\n    $js = preg_replace('!/\*.*?\*/!s', '', $js);\n    $js = preg_replace('/\n\s*\n/', "\n", $js);\n    \n    // Remove spaces and newlines\n    $js = preg_replace('/\s+/', ' ', $js);\n    $js = preg_replace('/\s*([{}|:;,])\s*/', '$1', $js);\n    $js = preg_replace('/;}/', '}', $js);\n    \n    return $js;\n}\n?>