$query = "SELECT u.url_id, url, title, description, \n          DATE_FORMAT(STR_TO_DATE(pub_date, '%d-%b-%Y'), '%d-%b-%Y') AS formatted_pub_date \n          FROM urls AS u, url_associations AS ua \n          WHERE u.url_id = ua.url_id \n          AND ua.url_category_id = $type \n          AND ua.approved = 'Y' \n          ORDER BY STR_TO_DATE(pub_date, '%d-%b-%Y') DESC";\n          \n$result = mysql_query($query);\n\nwhile ($row = mysql_fetch_assoc($result)) {\n    echo "  <tr>\n<td align=\"left\">{$row['formatted_pub_date']}</td>\n</tr>\n";\n}