$array = [/* your array elements */];\n$totalElements = count($array);\n$currentIndex = 0;\n\nforeach ($array as $element) {\n    if ($currentIndex === 0) {\n        // First iteration\n        echo "First iteration\n";\n    }\n\n    if ($currentIndex === $totalElements - 1) {\n        // Last iteration\n        echo "Last iteration\n";\n    }\n\n    // Regular code for each iteration\n    echo "Current element: " . $element . "\n";\n\n    $currentIndex++;\n}