$array = [/* your array elements */];\n$keys = array_keys($array);\n$firstKey = reset($keys);\n$lastKey = end($keys);\n\nforeach ($array as $key => $element) {\n    if ($key === $firstKey) {\n        // First iteration\n        echo "First iteration\n";\n    }\n\n    if ($key === $lastKey) {\n        // Last iteration\n        echo "Last iteration\n";\n    }\n\n    // Regular code for each iteration\n    echo "Current element: " . $element . "\n";\n}