// User selected product type\n$selected_product_type = 'banana';\n\n// Iterate through the list and filter results\nforeach ($list as $r) {\n    // Check if the product type starts with the selected product type\n    if (strpos($r[1], $selected_product_type) === 0) {\n        $row_color = ($row_count % 2) ? $color1 : $color2;\n        $size2 = $r[2];\n        echo "<tr>\n                <td id=\"id\"><span id=\"non_sorting_header\">" . htmlspecialchars($r[0], ENT_QUOTES, 'UTF-8') . "</span></td>\n                <td id=\"name\"><span id=\"non_sorting_header\">" . htmlspecialchars($r[1], ENT_QUOTES, 'UTF-8') . "</span></td>\n                <td id=\"speed\"><span id=\"sorting_header\">" . kMGTB2($size2) . "</span></td>\n                <td id=\"download\"><span id=\"sorting_header\">" . htmlspecialchars($r[3], ENT_QUOTES, 'UTF-8') . " Gb<br />per month</span></td>\n                <td id=\"contract\"><span id=\"sorting_header\">1<br />month</span></td>\n                <td id=\"info\"><span id=\"non_sorting_header\">" . htmlspecialchars($r[5], ENT_QUOTES, 'UTF-8') . "</span></td>\n                <td id=\"buy\"><span id=\"non_sorting_header\">" . htmlspecialchars($r[4], ENT_QUOTES, 'UTF-8') . "<br />" . htmlspecialchars($r[6], ENT_QUOTES, 'UTF-8') . "</span></td>\n              </tr>";\n        $row_count++;\n    }\n}