<?php\n// Path to the HTTP log file\n$logFilePath = '/path/to/your/httpd/logs/access_log';\n\n// Check if the log file is readable\nif (is_readable($logFilePath)) {\n    // Open the log file\n    $logFile = fopen($logFilePath, 'r');\n\n    if ($logFile) {\n        // Read log file line by line\n        while (($line = fgets($logFile)) !== false) {\n            // Display each line (you can process the line here)\n            echo htmlspecialchars($line) . "<br>";\n        }\n\n        // Close the log file\n        fclose($logFile);\n    } else {\n        echo "Unable to open the log file.";\n    }\n} else {\n    echo "Log file is not readable or does not exist.";\n}\n?>