$artists = array();\n\nfor ($i = 1; $i <= 20; $i++) {\n    $field_name = 'artist' . $i;\n    if (isset($_POST[$field_name])) {\n        // Sanitize input data\n        $artists[] = htmlspecialchars(trim($_POST[$field_name]), ENT_QUOTES, 'UTF-8');\n    }\n}\n\n// Example of inserting sanitized data into the database\n// Assuming $pdo is your PDO database connection\n\n$sql = "INSERT INTO artists (artist_name) VALUES (:artist_name)";\n$stmt = $pdo->prepare($sql);\n\nforeach ($artists as $artist) {\n    $stmt->execute(['artist_name' => $artist]);\n}