class person {\n    var $name;\n    var $email;\n\n    // Getters\n    function get_name() {\n        return $this->name;\n    }\n\n    function get_email() {\n        return $this->email;\n    }\n\n    // Setters\n    function set_name($name) {\n        $this->name = $name;\n    }\n\n    function set_email($email) {\n        if (!eregi("^([0-9,a-z,A-Z]+)([.,_,-]([0-9,a-z,A-Z]+))*[@]([0-9,a-z,A-Z]+)([.,_,-]([0-9,a-z,A-Z]+))*[.]([0-9,a-z,A-Z]){2}([0-9,a-z,A-Z])*$", $email)) {\n            return false;\n        } else {\n            $this->email = $email;\n            return true;\n        }\n    }\n}