function handleError($errorCode) {\n    switch ($errorCode) {\n        case 404:\n            header("Location: /path/to/custom_404_page.php");\n            break;\n        case 500:\n            header("Location: /path/to/custom_500_page.php");\n            break;\n        default:\n            header("Location: /path/to/custom_error_page.php");\n            break;\n    }\n    exit();\n}\n\n// Example usage\nhandleError(404);