$results = mysql_query("\n    SELECT id, question, correct, wrong1, wrong2, wrong3\n    FROM questions\n    ORDER BY id\n    LIMIT 40\n") or die("Oops, unable to access database at this time." . mysql_error());\n\n$questions = [];\nwhile ($row = mysql_fetch_array($results)) {\n    $answers = [\n        ['correct' => 1, 'answer' => $row['correct']],\n        ['correct' => 0, 'answer' => $row['wrong1']],\n        ['correct' => 0, 'answer' => $row['wrong2']],\n        ['correct' => 0, 'answer' => $row['wrong3']]\n    ];\n\n    // Randomize the answers\n    shuffle($answers);\n\n    $questions[] = [\n        'id' => $row['id'],\n        'question' => $row['question'],\n        'answers' => $answers\n    ];\n}\n\n// Store the randomized questions in the session\n$_SESSION['questions'] = $questions;\n?>