<?php\nclass Zend_View_Helper_LoginWidget extends Zend_View_Helper_Abstract\n{\n    public function loginWidget()\n    {\n        $flashMessenger = Zend_Controller_Action_HelperBroker::getStaticHelper('FlashMessenger');\n        $flashMessenger->setNamespace('login');\n        \n        $messages = $flashMessenger->getMessages();\n        \n        // Render the login widget with messages\n        return $this->view->partial('partials/login-widget.phtml', ['messages' => $messages]);\n    }\n}