$mysqli = new mysqli("localhost", "user", "password", "database");\n\n$stmt = $mysqli->prepare("INSERT INTO table (col1, col2, col3) VALUES (?, ?, ?)");\n\n$typeString = "ssi"; // The types of the parameters (string, string, integer)\n$param1 = "value1";\n$param2 = "value2";\n$param3 = 123;\n\n$array_of_params = array_merge(array($typeString), array(&$param1, &$param2, &$param3));\n\n// Dynamically call bind_param with the array of parameters\ncall_user_func_array(array(&$stmt, 'bind_param'), $array_of_params);\n\n$stmt->execute();\n$stmt->close();\n$mysqli->close();