<?php\nrequire 'vendor/autoload.php';\n\n$app = new \Slim\App;\n\n$app->get('/', function ($request, $response, $args) {\n    $response->getBody()->write("\n        <!DOCTYPE html>\n        <html>\n        <head>\n            <title>Slim with Dojo</title>\n            <script src='https://ajax.googleapis.com/ajax/libs/dojo/1.16.3/dojo/dojo.js'></script>\n        </head>\n        <body>\n            <h1>Hello, Slim Framework!</h1>\n            <script>\n                require(['dojo/dom', 'dojo/domReady!'], function(dom){\n                    var node = dom.byId('content');\n                    node.innerHTML = 'Dojo is now loaded!';\n                });\n            </script>\n            <div id='content'></div>\n        </body>\n        </html>\n    ");\n    return $response;\n});\n\n$app->run();