function someFunctionToOffsetEvents($events) {\n    // Sort events by start time\n    usort($events, function($a, $b) {\n        return $a['start'] - $b['start'];\n    });\n\n    // Check for overlapping events\n    $eventCount = count($events);\n    for ($i = 0; $i < $eventCount; $i++) {\n        for ($j = $i + 1; $j < $eventCount; $j++) {\n            if ($events[$j]['start'] < $events[$i]['end']) {\n                // Overlapping events found\n                $events[$i]['split'] = true;\n                $events[$j]['split'] = true;\n            } else {\n                // No more overlaps possible with sorted events\n                break;\n            }\n        }\n    }\n\n    return $events;\n}\n\n// Example array containing two simultaneous events\n$events = array(\n    array(\n        "id" => 21,\n        "start" => 1242219600,\n        "end" => 1242237600,\n        "title" => "foo",\n        "split" => false\n    ),\n    array(\n        "id" => 22,\n        "start" => 1242223200,\n        "end" => 1242234000,\n        "title" => "foo",\n        "split" => false\n    )\n);\n\n$events = someFunctionToOffsetEvents($events);\n\n// Print resulting events array\nprint_r($events);