<?php\n\n   class YourCompany_YourModule_Model_MyModel extends Mage_Core_Model_Abstract\n   {\n       protected function _construct()\n       {\n           $this->_init('yourmodule/mymodel');\n       }\n\n       public function getDataFromTable()\n       {\n           $connection = Mage::getSingleton('core/resource')->getConnection('core_read');\n           $tableName = Mage::getSingleton('core/resource')->getTableName('your_table');\n\n           $select = $connection->select()\n                                ->from($tableName, ['*']);\n\n           $results = $connection->fetchAll($select);\n           return $results;\n       }\n   }