<?php\nclass Database {\n    private $pdo;\n\n    public function __construct($dsn, $username, $password) {\n        try {\n            $this->pdo = new PDO($dsn, $username, $password);\n            $this->pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n        } catch (PDOException $e) {\n            die("Database connection failed: " . $e->getMessage());\n        }\n    }\n\n    public function sanitizeAndExecute($query, $params) {\n        try {\n            $stmt = $this->pdo->prepare($query);\n            $stmt->execute($params);\n            return $stmt->fetchAll(PDO::FETCH_ASSOC);\n        } catch (PDOException $e) {\n            die("Query failed: " . $e->getMessage());\n        }\n    }\n}\n\n// Example usage\n$db = new Database('mysql:host=localhost;dbname=testdb', 'username', 'password');\n$query = "SELECT * FROM users WHERE username = :username AND status = :status";\n$params = [\n    ':username' => $_POST['username'],\n    ':status' => $_POST['status']\n];\n$result = $db->sanitizeAndExecute($query, $params);\n\nprint_r($result);\n?>