<?php\n// Assuming you have already established a connection to your database in $pdo\n$current_post_id = 1; // Replace with the actual current post ID\n\n$sql = "\n    SELECT p2.id, p2.title, COUNT(pt2.tag_id) AS common_tag_count\n    FROM Posts p\n    JOIN PostTags pt1 ON pt1.post_id = :current_post_id\n    JOIN PostTags pt2 ON pt2.tag_id = pt1.tag_id AND pt2.post_id != :current_post_id\n    JOIN Posts p2 ON p2.id = pt2.post_id\n    GROUP BY p2.id, p2.title\n    ORDER BY common_tag_count DESC;\n";\n\n$stmt = $pdo->prepare($sql);\n$stmt->execute(['current_post_id' => $current_post_id]);\n$related_posts = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nforeach ($related_posts as $post) {\n    echo "Post ID: " . $post['id'] . " - Title: " . $post['title'] . " - Common Tags: " . $post['common_tag_count'] . "\n";\n}\n?>