<?php\n// Connect to the database\n$connection = mysqli_connect('hostname', 'username', 'password', 'database');\n\n// Check connection\nif (!$connection) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n// Query to get category groups, categories, and the count of news stories\n$query = "\n    SELECT \n        cg.cat_group_id, \n        cg.cat_group_name, \n        c.category_id, \n        c.title, \n        COUNT(n.news_id) AS news_count\n    FROM \n        CategoryGroups cg\n    LEFT JOIN \n        Categories c ON cg.cat_group_id = c.cat_group_id\n    LEFT JOIN \n        News n ON c.category_id = n.category_id\n    GROUP BY \n        cg.cat_group_id, \n        c.category_id\n    ORDER BY \n        cg.cat_group_id, \n        c.category_id\n";\n\n$result = mysqli_query($connection, $query);\n\n// Check query result\nif (!$result) {\n    die("Query failed: " . mysqli_error($connection));\n}\n\n// Process the results\n$current_cat_group_id = null;\nwhile ($row = mysqli_fetch_assoc($result)) {\n    if ($row['cat_group_id'] !== $current_cat_group_id) {\n        if ($current_cat_group_id !== null) {\n            echo "<hr/>";\n        }\n        $current_cat_group_id = $row['cat_group_id'];\n        echo "<h3>{$row['cat_group_name']}</h3>";\n    }\n    \n    echo "{$row['title']} ({$row['news_count']})<br/>";\n}\n\n// Close connection\nmysqli_close($connection);\n?>