<?php\n// User's preferred timezone, e.g., 'America/New_York', 'Europe/London'\n$userTimezone = 'Europe/London'; // Example timezone\n\n// Create a new DateTime object in the server's default timezone\n$date = new DateTime('now', new DateTimeZone('EST'));\n\n// Change the timezone to the user's preferred timezone\n$date->setTimezone(new DateTimeZone($userTimezone));\n\n// Format the date/time to your desired format, e.g., 'Y-m-d H:i:s'\necho $date->format('Y-m-d H:i:s');\n?>