<?php\nsession_start();\n\n// Database connection using PDO\ntry {\n    $pdo = new PDO('mysql:host=localhost;dbname=quiz', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->prepare("SELECT id, question, correct, wrong1, wrong2, wrong3 FROM questions ORDER BY id LIMIT 40");\n    $stmt->execute();\n\n    $questions = [];\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        $answers = [\n            ['correct' => 1, 'answer' => $row['correct']],\n            ['correct' => 0, 'answer' => $row['wrong1']],\n            ['correct' => 0, 'answer' => $row['wrong2']],\n            ['correct' => 0, 'answer' => $row['wrong3']]\n        ];\n\n        // Randomize the answers\n        shuffle($answers);\n\n        $questions[] = [\n            'id' => $row['id'],\n            'question' => $row['question'],\n            'answers' => $answers\n        ];\n    }\n\n    // Store the randomized questions in the session\n    $_SESSION['questions'] = $questions;\n\n} catch (PDOException $e) {\n    die("Database error: " . $e->getMessage());\n}\n?>