<?php\n// Database connection\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// SQL query to fetch the serialized array\n$sql = "SELECT array_column FROM your_table WHERE id = ?";\n$stmt = $mysqli->prepare($sql);\n$id = 1; // Assuming you are fetching the row with ID 1\n$stmt->bind_param("i", $id);\n\n$stmt->execute();\n$stmt->bind_result($serializedArray);\n$stmt->fetch();\n\n$stmt->close();\n$mysqli->close();\n\nif ($serializedArray !== null) {\n    // Unserialize the array\n    $array = unserialize($serializedArray);\n    \n    // Display the array\n    var_dump($array);\n} else {\n    echo "No data found.";\n}\n?>