<?php\n// Load the XML file\n$xml = simplexml_load_file('path/to/your/file.xml');\n\n// Check if the XML was loaded correctly\nif ($xml === false) {\n    echo "Failed to load XML file.";\n    foreach(libxml_get_errors() as $error) {\n        echo "<br>", $error->message;\n    }\n} else {\n    // Successfully loaded the XML, now you can process it\n    // For example, loop through items in the XML\n    foreach ($xml->item as $item) {\n        echo $item->name . "<br>";\n    }\n}\n?>