<?php\n$some_php_document = $_POST["some_value_submitted_via_ajax"];\n\n// Define the pattern for a relative path (no URL, no absolute path)\n$pattern = '/^(?!.*(?:\/\/|http:\/\/|https:\/\/|ftp:\/\/|file:\/\/|\\\\|:)).*$/';\n\n// Validate the input\nif (preg_match($pattern, $some_php_document)) {\n    // Input is a relative path\n    include($some_php_document);\n} else {\n    // Input is not a relative path\n    $some_php_document = null;\n    // Handle the error appropriately\n    die("Invalid input: Absolute path or URL is not allowed.");\n}\n?>