<?php\n// Path to the Perl script\n$perlScript = '/path/to/your/script.pl';\n\n// Execute the Perl script and capture the output\n$output = [];\n$returnVar = 0;\nexec("perl $perlScript", $output, $returnVar);\n\n// Check if the script was executed successfully\nif ($returnVar === 0) {\n    // Output the result\n    foreach ($output as $line) {\n        echo htmlspecialchars($line) . "<br>";\n    }\n} else {\n    echo "Error executing Perl script.";\n}\n?>