$pattern = '/^(https?:\/\/)?(www\.)?([a-zA-Z0-9-]+(\.[a-zA-Z0-9-]+)+)(\/.*)?$/';\n\n// Test strings\n$websites = [\n    'somewebsite.com',\n    'http://somewebsite.com',\n    'http://www.somewebsite.com',\n    'somewebsite.com/',\n    'sub.somewebsite.com',\n    'http://sub.somewebsite.com/',\n];\n\nforeach ($websites as $website) {\n    if (preg_match($pattern, $website)) {\n        echo "Valid: $website\n";\n    } else {\n        echo "Invalid: $website\n";\n    }\n}