$urls = []; // Array of all the URLs you need to request\nfor ($i = 0; $i < 1500; $i++) {\n    $urls[] = "http://www.mysite.com/3rdparty/API/api.php?fn=post&username=test&password=tester&url=http://google.com&category=21&title=story_$i&content=content_text_$i&tags=Season,news";\n}\n\n$multiCurl = [];\n$result = [];\n$mh = curl_multi_init();\n\nforeach ($urls as $i => $url) {\n    $multiCurl[$i] = curl_init();\n    curl_setopt($multiCurl[$i], CURLOPT_URL, $url);\n    curl_setopt($multiCurl[$i], CURLOPT_RETURNTRANSFER, true);\n    curl_setopt($multiCurl[$i], CURLOPT_COOKIEFILE, 'files/cookie.txt');\n    curl_setopt($multiCurl[$i], CURLOPT_FOLLOWLOCATION, false);\n    curl_multi_add_handle($mh, $multiCurl[$i]);\n}\n\n$index = null;\ndo {\n    curl_multi_exec($mh, $index);\n} while ($index > 0);\n\n// Collect results and remove handles\nforeach ($multiCurl as $i => $ch) {\n    $result[$i] = curl_multi_getcontent($ch);\n    curl_multi_remove_handle($mh, $ch);\n    curl_close($ch);\n}\n\ncurl_multi_close($mh);\n\n// Process results\nforeach ($result as $response) {\n    // Handle each response\n    echo $response . "\n";\n}