<?php\n// Your multidimensional array\n$array = array(\n    "Part1" => array(\n        "Subpart1" => array(0, 1),\n        "Subpart2" => array(1, 0)\n    ),\n    "Part2" => array(0),\n    "Part3" => array(0, 1, 0)\n);\n\n// Serialize the array\n$serializedArray = serialize($array);\n\n// Database connection\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// SQL query to insert the serialized array\n$sql = "INSERT INTO your_table (array_column) VALUES (?)";\n$stmt = $mysqli->prepare($sql);\n$stmt->bind_param("s", $serializedArray);\n\nif ($stmt->execute()) {\n    echo "Array stored successfully.";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n$stmt->close();\n$mysqli->close();\n?>