function decryptBlowfishCBC($encryptedData, $key, $iv) {\n    $key = substr($key, 0, mcrypt_get_key_size(MCRYPT_BLOWFISH, MCRYPT_MODE_CBC));\n    \n    $td = mcrypt_module_open(MCRYPT_BLOWFISH, '', MCRYPT_MODE_CBC, '');\n    mcrypt_generic_init($td, $key, $iv);\n    \n    $decryptedData = mdecrypt_generic($td, base64_decode($encryptedData));\n    $decryptedData = rtrim($decryptedData, "\0");  // Remove any null padding\n    \n    mcrypt_generic_deinit($td);\n    mcrypt_module_close($td);\n    \n    return $decryptedData;\n}\n\n// Example usage:\n$encryptedData = '...'; // Base64 encoded encrypted data\n$key = 'ThisIsMyKey';\n$iv = '...'; // IV used during encryption\n\n$decryptedText = decryptBlowfishCBC($encryptedData, $key, $iv);\necho $decryptedText;