<?php\nsession_start();\n\nfunction addStatusMsg($msg) {\n    if (!isset($_SESSION['status_msgs'])) {\n        $_SESSION['status_msgs'] = [];\n    }\n    $_SESSION['status_msgs'][] = $msg;\n}\n\nfunction displayStatusMsg() {\n    if (isset($_SESSION['status_msgs']) && !empty($_SESSION['status_msgs'])) {\n        echo '<div class="status-messages">';\n        foreach ($_SESSION['status_msgs'] as $msg) {\n            echo '<p>' . htmlspecialchars($msg) . '</p>';\n        }\n        echo '</div>';\n        // Clear messages after displaying\n        unset($_SESSION['status_msgs']);\n    }\n}\n?>