<?php\n$xmlString = <<<XML\n<favorites>\n    <interesting>\n        <type>Movie</type>\n        <character>James Bond</character>\n        <name>Casino Royale</name>\n    </interesting>\n    <interesting>\n        <type>Movie</type>\n        <character>Jason Bourne</character>\n        <name>Bourne Identity</name>\n    </interesting>\n    <interesting>\n        <type>Book</type>\n        <character>Lindsay Ford</character>\n        <name>Shantaram</name>\n    </interesting>\n</favorites>\nXML;\n\n$xml = new SimpleXMLElement($xmlString);\n$groupedData = [];\n\n// Group by type\nforeach ($xml->interesting as $item) {\n    $type = (string) $item->type;\n    $name = (string) $item->name;\n    $character = (string) $item->character;\n    \n    $groupedData[$type][] = "$name - $character";\n}\n\n// Output the grouped data\nforeach ($groupedData as $type => $items) {\n    echo "<strong>$type</strong>\n<ul>\n";\n    foreach ($items as $item) {\n        echo "<li>$item</li>\n";\n    }\n    echo "</ul>\n";\n}\n?>