function splitArrayByBlankLine($array) {\n    $result = [];\n    $currentSection = [];\n\n    foreach ($array as $line) {\n        if (trim($line) === '') {\n            if (!empty($currentSection)) {\n                $result[] = $currentSection;\n                $currentSection = [];\n            }\n        } else {\n            $currentSection[] = $line;\n        }\n    }\n\n    // Add the last section if it exists\n    if (!empty($currentSection)) {\n        $result[] = $currentSection;\n    }\n\n    return $result;\n}\n\n// Example usage:\n$file = 'path/to/your/file.txt';\n$data = file($file) or die('Could not read file!');\n$sections = splitArrayByBlankLine($data);\n\nprint_r($sections);