class MyAutoloader {\n    public static function autoload($class) {\n        $class = str_replace('Controller_', '', $class);\n        $class = str_replace('_', DIRECTORY_SEPARATOR, $class);\n        $file = APPLICATION_PATH . '/controllers/' . $class . '.php';\n        if (file_exists($file)) {\n            require_once $file;\n        }\n    }\n}\n\nspl_autoload_register(array('MyAutoloader', 'autoload'));