class TU {\n    public static function suite() {\n        $suite = new PHPUnit_Framework_TestSuite('My Test Suite');\n\n        // Add individual test files\n        $suite->addTestFile('/path/to/test1.php');\n        $suite->addTestFile('/path/to/test2.php');\n        \n        // You can also add entire directories\n        $suite->addTestDirectory('/path/to/tests/');\n\n        return $suite;\n    }\n\n    public static function main() {\n        PHPUnit_TextUI_TestRunner::run(self::suite());\n    }\n}