$page_count = 10; // in actual code this is set properly\n$current_page = 1; // in actual code this is set properly\n\n// Draw the first three pages\nfor ($i = 1; $i <= 3; $i++) {\n    if ($page_count >= $i) {\n        echo $i . ' ';\n    }\n}\n\n// Draw the first ellipsis if needed\nif ($page_count > 5 && $current_page > 5) {\n    echo "... ";\n}\n\n// Draw the pages around the current page\nfor ($i = max(4, $current_page - 2); $i <= min($page_count - 3, $current_page + 2); $i++) {\n    echo $i . ' ';\n}\n\n// Draw the second ellipsis if needed\nif ($page_count > 5 && $current_page < $page_count - 4) {\n    echo "... ";\n}\n\n// Draw the last three pages\nfor ($i = max($page_count - 2, 4); $i <= $page_count; $i++) {\n    if ($page_count > 3) {\n        echo $i . ' ';\n    }\n}