private function GenerateImage()\n{\n    $original = imagecreatefrompng($this->ImagePath());\n\n    $x = imagesx($original);\n    $y = imagesy($original);\n\n    // Create a true color image with alpha channel\n    $image = imagecreatetruecolor($x, $y);\n\n    // Preserve transparency\n    imagealphablending($image, false);\n    imagesavealpha($image, true);\n\n    // Fill the new image with the fill color\n    $colour = imagecolorallocatealpha($image, $this->RGB[0], $this->RGB[1], $this->RGB[2], 127); // 127 is full transparency\n    imagefill($image, 0, 0, $colour);\n\n    // Copy the original image onto the new image\n    imagecopy($image, $original, 0, 0, 0, 0, $x, $y);\n\n    // Save the final image\n    imagepng($image, $this->GeneratedPath());\n\n    imagedestroy($original);\n    imagedestroy($image);\n}